import * as vars from '@/core/Theme/vars';
import Typography from 'antd/lib/typography';
import React from 'react';
import { Ripple } from 'react-awesome-spinners';
import { AppContainer } from '@/core/Components/AppContainer';
import { Post } from '@/core/http/requests';
import { useRouter } from 'next/router';
import * as styles from './styles';

const { Title } = Typography;

type State = {
  loading: boolean;
};

type Props = {
  token: string;
};

class Authenticate extends React.Component<Props, State> {
  constructor(props) {
    super(props);
    this.state = {
      loading: true,
    };
  }

  async componentDidMount() {
    const router = useRouter();
    const { token } = router.query;
    console.log(token);

    const res = Post('/authenticate', {
      Token: token,
    });

    console.log('componentDidMount', res);
  }

  componentWillUnmount(): void {
    console.log('componentWillUnmount');
  }

  render(): any {
    const { loading } = this.state;
    return (
      <div>
        <styles.Root>
          {loading && (
            <styles.Container>
              <Ripple color={vars.Blue6} /> <br />
              <Typography.Paragraph>
                {' '}
                We are securely logging you in, please wait ...
              </Typography.Paragraph>
            </styles.Container>
          )}
        </styles.Root>
        <styles.Info>
          <Title
            level={4}
            style={{
              fontWeight: 'normal',
              color: vars.HeadingColor1,
              margin: 0,
              fontSize: '18px',
              textAlign: 'center',
              paddingTop: '25px',
              paddingBottom: '25px',
            }}
          >
            <AppContainer>
              After submitting login details, we&apos;ll send you an email link
              to login .
            </AppContainer>
          </Title>
        </styles.Info>
      </div>
    );
  }
}
export default Authenticate;
