import React, { Component } from 'react';
import Typography from 'antd/lib/typography';
import Link from 'next/link';
import { NextRouter } from 'next/router';
import { ValidateEmail } from '@/core/validations/email';

import Button from '@atlaskit/button';
import Form, {
  ErrorMessage,
  Field,
  FormFooter,
  ValidMessage,
} from '@atlaskit/form';

import Textfield from '@atlaskit/textfield';
import * as styles from './styles';

const { Title } = Typography;

type Props = {
  router: NextRouter;
  authenticate: any;
};

type State = {
  email: string;
  valid: boolean;
};

// eslint-disable-next-line import/no-anonymous-default-export
export default class extends Component<Props, State> {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      valid: true,
    };
  }

  handleSubmit = (): void => {
    // you can now do stuff with the form.
  };

  onChange = (e: unknown): void => {
    console.log(e);
  };

  render(): any {
    const { email, valid } = this.state;
    return (
      <styles.Root>
        <Form onSubmit={this.handleSubmit}>
          {({ formProps }) => (
            <form {...formProps} name="validation-example">
              <Field
                label="Work Email"
                isRequired
                name="work-email"
                defaultValue=""
              >
                {({ fieldProps }: any) => (
                  <>
                    <Textfield
                      {...fieldProps}
                      value={email}
                      onChange={this.onChange}
                    />
                    {!valid && (
                      <ErrorMessage>
                        Incorrect, try &lsquo;open sesame&rsquo;
                      </ErrorMessage>
                    )}
                  </>
                )}
              </Field>
              <FormFooter>
                <Button
                  style={{ height: '40px' }}
                  shouldFitContainer
                  type="submit"
                  appearance="primary"
                >
                  Login
                </Button>
              </FormFooter>
            </form>
          )}
        </Form>
        <Title
          level={5}
          style={{
            marginTop: '20px',
            fontWeight: 500,
            textAlign: 'center',
          }}
        >
          Don&apos;t have an account?{' '}
          <Link href="/signup">
            <a href="/signup">Register</a>
          </Link>
        </Title>
      </styles.Root>
    );
  }
}
