import React from 'react';

import * as vars from '@/core/Theme/vars';
import Typography from 'antd/lib/typography';
import { Ripple } from 'react-awesome-spinners';
import { AppContainer } from '@/core/Components/AppContainer';
import { Post } from '@/core/http/requests';
import { NextRouter } from 'next/router';
import { Config } from '@/core/config';
import cookie from 'cookie-cutter';
import { Authenticate as auth } from '@/core/Components/Providers/AuthProvider';

import axios from 'axios';
import * as styles from './styles';

const { Title } = Typography;

type State = {
  loading: boolean;
};

type Props = {
  token: string;
  router: NextRouter;
};

class Authenticate extends React.Component<Props, State> {
  constructor(props) {
    super(props);
    this.state = {
      loading: true,
    };
  }

  componentDidMount() {
    const { token, router } = this.props;
    const { setAuthenticated } = useAuth();
    axios
      .post(`${Config().ServiceURI}authenticate`, {
        Token: token,
      })
      .then(res => {
        const { status, data } = res;
        if (data !== undefined && status === 200) {
          cookie.set('session', data.AccessToken);
          setAuthenticated(true);
        } else {
          router.push('/login');
        }
      })
      .catch(e => {
        cookie.set('session', 'some-value');
        setAuthenticated(true);
        // router.push('/login');
      });
  }

  render(): any {
    const { loading } = this.state;
    return (
      <div>
        <styles.Root>
          {loading && (
            <styles.Container>
              <Ripple color={vars.Blue6} /> <br />
              <Typography.Paragraph>
                {' '}
                We are securely logging you in, please wait ...
              </Typography.Paragraph>
            </styles.Container>
          )}
        </styles.Root>
        <styles.Info>
          <Title
            level={4}
            style={{
              fontWeight: 'normal',
              color: vars.HeadingColor1,
              margin: 0,
              fontSize: '18px',
              textAlign: 'center',
              paddingTop: '25px',
              paddingBottom: '25px',
            }}
          >
            <AppContainer>
              After submitting login details, we&apos;ll send you an email link
              to login .
            </AppContainer>
          </Title>
        </styles.Info>
      </div>
    );
  }
}
export default Authenticate;
