import React, { ReactElement } from 'react';
import App, { AppInitialProps } from 'next/app';
import Cookies from 'universal-cookie';
import '@/core/Theme/antd.less';
import '@/core/Theme/base';
import { createGlobalStyle, ThemeProvider } from 'styled-components';

import { CookieMessage } from '@/core/types/index.d';
import { AuthProvider } from '@/core/Components/Providers/AuthProvider';

const GlobalStyle = createGlobalStyle``;
const theme = {};

type AppProps = {
  authenticated: boolean;
};

class MyApp extends App<AppProps> {
  render(): ReactElement {
    const { Component, pageProps, authenticated } = this.props;
    return (
      <div>
        <GlobalStyle />
        <ThemeProvider theme={theme}>
          <AuthProvider authenticated={authenticated}>
            <Component {...pageProps} />
          </AuthProvider>
        </ThemeProvider>
      </div>
    );
  }
}

MyApp.getInitialProps = async (
  appContext,
): Promise<AppInitialProps & AppProps> => {
  let authenticated = false;
  const request = appContext.ctx.req as CookieMessage;
  if (request) {
    const cookies = new Cookies(request.headers.cookie || '');
    request.cookies = cookies.getAll() || {};

    authenticated = !!request.cookies.session;
  }

  // TODO  Do the Cookie Validation here via API call

  // Call the page's `getInitialProps` and fill `appProps.pageProps`
  const appProps = await App.getInitialProps(appContext);
  return { ...appProps, authenticated };
};

export default MyApp;

async function authenticate(token: string): boolean {
  axios
    .post(`${Config().ServiceURI}/login`, {
      Email: email,
    })
    .then(res => {
      const { status, data } = res;
      if (data !== undefined && status === 200) {
        successNotification(
          `A login link has been successfully sent on the email address ${email}. Please click on the link to proceed. `,
        );
        this.setState({ email: '' });
      } else {
        errorNotification(data.Error);
      }
    })
    .catch(error => {
      const { response } = error;
      if (response !== undefined && response.data !== undefined) {
        errorNotification(response.data.Error);
      } else {
        errorNotification('Something went wrong, please try again');
      }
    });
  return false;
}
